import { FooterComponent } from '../footer/footer.component';
export class HeaderComponent {
  constructor(dialog) {
    this.dialog = dialog;
  }

  openDialog() {
    const dialogRef = this.dialog.open(FooterComponent, {
      width: '250px',
      data: {
        name: this.name,
        animal: this.animal
      }
    });
    dialogRef.afterClosed().subscribe(result => {
      console.log('The dialog was closed');
      this.animal = result;
    });
  }

}
